import React from 'react';
import { RotatingLines } from 'react-loader-spinner';

export default function SpinningLoader({ isLoading }) {
    if (isLoading) return (
        <div className={LoaderContainer}>
            <div className={Loader}>
                <RotatingLines
                    strokeColor="grey"
                    // strokeWidth="5"
                    // animationDuration="0.75"
                    width="96"
                />
            </div>
        </div>
    );
}
