export default async function apiCaller({ url, method = 'get', headers, body }) {
    const response = await fetch(url, {
        method,
        headers: {
            Authorization: `Bearer ${process.env.REACT_APP_TOKEN}`,
            ...headers,
        },
        body: JSON.stringify(body)
    });
    const data = await response.json();
    console.log(data);
}
