import React, { useState } from 'react';
import styles from './Chatter.module.scss';
import Messages from '../Messages/Messages';

const {
    MainContainer,
    Heading,
    Label,
    InputContainer,
    PostBtn,
    DelBtn
} = styles;

export default function Chatter() {
    const [] = useState();

    const onChange = val => setText(val);

    const postMessage = () => {};

    return (
        <div className={MainContainer}>
            <div className={Heading}>Chatter</div>
            <div className={Label}>Type something in the box below, then hit "Post"</div>
            <div className={InputContainer}>
                <input
                    type='text'
                    onChange={e => onChange(e.target.value)}
                />
                <button className={PostBtn} onClick={postMessage}>Post!</button>
                <button className={DelBtn}>Delete All</button>
            </div>
            <Messages />
        </div>
    );
}
