import React from 'react';
import styles from './Message.module.scss';
import moment from 'moment';
import apiCaller from '../../apiCaller';

const {
    MainContainer,
    SenderContainer,
    Sender,
    Time
} = styles;

export default function Message({ message, onDeleteSuccess }) {
    const deleteMessage = (e) => {
        e.stopPropagation();
        e.preventDefault();
        apiCaller({
            url: `https://mapi.harmoney.dev/api/v1/messages/${message.id}/`,
            method: 'delete'
        }).then(onDeleteSuccess).catch((err) => {
            alert('Some error occurred!');
            console.error(err);
        })
    }

    return (
        <div className={MainContainer}>
            <div className={SenderContainer}>
                <span className={Sender}>~{message.source}</span>
                {' '}
                <span className={Time}>- {moment(message.timestamp).local().format('hh:mm:ss a')}</span>
                {' '}
                <a href='#' onClick={deleteMessage}>Delete</a>
            </div>
            <div>{message.text}</div>
        </div>
    );
}
