export default async function apiCaller({ url, method = 'get', headers, body }) {
    const response = await fetch(url, {
        method,
        headers: {
            key: "Authorization",
            value: "{{token}}",
            type: "text",
            // token: ,
            ...headers,
        },
        body: JSON.stringify(body)
    });
    const data = await response.json();
    console.log(data);
}
