import React from 'react';
import { Loader } from 'react-loader-spinner';

export default function SpinningLoader({ isLoading }) {
    if (isLoading) return (
        <Loader
            type="Puff"
            color="#00BFFF"
            height={100}
            width={100}
            // timeout={3000}
        />
    );
}
