export default async function apiCaller({ url, method = 'get', headers, body }) {
    try {
        const response = await fetch(url, {
            method,
            headers: {
                ...headers,
                'Access-Control-Request-Headers': 'Authorization',
                'Authorization': 'Bearer ' + "uARVpoykxIguwR0f",
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*',
                'Cache-Control': 'no-cache',
            },
            mode: 'no-cors', // Uncomment or omit this line based on your needs
            credentials: 'include', // Uncomment if credentials are needed
        });

        if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        console.log(data);
    } catch (error) {
        console.error('Error:', error);
    }
}
