import React, { useState } from 'react';
import styles from './Chatter.module.scss';
import Messages from '../Messages/Messages';
import apiCaller from '../../apiCaller';

const {
    MainContainer,
    Heading,
    Label,
    InputContainer,
    PostBtn,
    DelBtn
} = styles;

export default function Chatter() {
    const [text, setText] = useState();

    const onChange = val => setText(val);

    const fetchMessages = async() => {
        let messages = [];
        try {
            messages = await apiCaller({
                url: 'https://mapi.harmoney.dev/api/v1/messages/'
            });
        }
        catch (err) {
            console.error(err);
        }
        finally {
            return messages;
        }
    }

    const postMessage = () => {
        if (text) {
            apiCaller({
                url: 'https://mapi.harmoney.dev/api/v1/messages/',
                method: 'post',
                body: {
                    text
                }
            }).then(fetchMessages).catch((err) => {
                alert('Some error occurred!');
                console.error(err);
            });
        }
    };

    return (
        <div className={MainContainer}>
            <div className={Heading}>Chatter</div>
            <div className={Label}>Type something in the box below, then hit "Post"</div>
            <div className={InputContainer}>
                <input
                    type='text'
                    onChange={e => onChange(e.target.value)}
                />
                <button className={PostBtn} onClick={postMessage}>Post!</button>
                <button className={DelBtn}>Delete All</button>
            </div>
            <Messages fetchMessages={fetchMessages} />
        </div>
    );
}
